(function () {
    "use strict";

    if (!("visibilityState" in document)) {
        return;
    }

   function initCallBuilder() {
        var elements;

        function buildPluginCall() {
            var opts;

            opts = [];
            ["width", "height"].forEach(function (key) {
                if (elements[key].value !== "") {
                    opts.push(key + '=' + elements[key].value);
                }
            });
            opts.push("preload=" + elements.preload.value);
            opts.push("resize=" + elements.resize.value);
            ["autoplay", "loop", "controls"].forEach(function (key) {
                opts.push(key + "=" + (elements[key].checked ? "1" : "0"));
            });
            elements.call.value = "{{{PLUGIN:video('" + elements.name.value + "', '"
                + opts.join('&') + "');}}}";
        }

        function selectContent() {
            this.select();
        }

        var template = document.getElementById("video_call_builder");
        template.outerHTML = template.text;

        elements = {};
        ["name", "preload", "autoplay", "loop", "controls", "width", "height", "resize", "call"].forEach(function (key) {
            var element = document.getElementById("video_" + key);
            elements[key] = element;
            if (key !== "call") {
                element.onchange = buildPluginCall;
            } else {
                element.onclick = selectContent;
            }
        });
        buildPluginCall();
    };

    if (document.getElementById("video_call_builder")) {
        initCallBuilder();
    }
}());
