(function () {
    "use strict";

    if (!("visibilityState" in document)) {
        return;
    }

    var each = Array.prototype.forEach;

   function initCallBuilder() {
        function buildPluginCall() {
            var opts = [];
            each.call(form.elements, function (element) {
                if (element.type === "checkbox") {
                    opts.push(element.id.substr(6) + "=" + (element.checked ? "1" : "0"));
                } else if (["video_name", "video_call"].indexOf(element.id) === -1) {
                    opts.push(element.id.substr(6) + '=' + encodeURIComponent(element.value).replace("'", "%27"));
                }
            });
            form.elements.video_call.value = "{{{video('" + encodeURIComponent(form.elements.video_name.value) +
                    "','" + opts.join("&") + "')}}}";
        }

        function selectContent() {
            this.select();
        }

        function parsePluginCall() {
            var text = form.elements.video_call.value;
            var matches = text.match(/'([^'])*'/g);
            if (matches && matches.length === 2) {
                form.reset();
                form.elements.video_call.value = text;
                var name = matches[0].substr(1, matches[0].length - 2);
                document.getElementById("video_name").value = name;
                var options = matches[1].substr(1, matches[1].length - 2).split("&");
                options.forEach(function (option) {
                    var pair = option.split("=");
                    if (pair.length === 2) {
                        var element = document.getElementById("video_" + pair[0]);
                        if (element) {
                            if (element.type === "checkbox") {
                                element.checked = +pair[1];
                            } else {
                                element.value = decodeURIComponent(pair[1]);
                            }
                        }
                    }
                });
                buildPluginCall();
            }
        }

        var template = document.getElementById("video_call_builder");
        var clone = template.content.cloneNode(true);
        template.parentElement.insertBefore(clone, template);

        var form = document.getElementById("video_call_builder");
        each.call(form.elements, function (element) {
            if (element.id !== "video_call") {
                element.onchange = buildPluginCall;
            } else {
                element.onclick = selectContent;
                element.onchange = parsePluginCall;
            }
        });

        buildPluginCall();
    };

    if (document.getElementById("video_call_builder")) {
        initCallBuilder();
    }
}());
